package configuration

import (
	"github.com/stretchr/testify/assert"
	"testing"
)

func TestUserEmailWithAValidAccessToken(t *testing.T) {
	config := Configuration{
		AccessToken: "bearer eyJhbGciOiJSUzI1NiJ9..kjFJHi0Qir9kfqi2eyhHy6kdewhicAFu8hrPR1a5AxFvxGB45slKEjuP0_72cM_vEYICgZn3PcUUkHU9wghJO9wjZ6kiIKK1h5f2K9g-Iprv9BbTOWUODu1HoLIvg2TtGsINxcRYy_8LW1RtvQc1b4dBPoopaEH4no-BIzp0E5E",
	}

	assert.Equal(t, config.UserEmail(), "user1@example.com")
}

func TestUserEmailWithInvalidAccessToken(t *testing.T) {
	config := Configuration{}

	config.AccessToken = "bearer"
	assert.Empty(t, config.UserEmail())

	config.AccessToken = "bearer eyJhbGciOiJSUzI1NiJ9"
	assert.Empty(t, config.UserEmail())
}

func TestUserGuidWithAValidAccessToken(t *testing.T) {
	config := Configuration{
		AccessToken: "bearer eyJhbGciOiJSUzI1NiJ9..kjFJHi0Qir9kfqi2eyhHy6kdewhicAFu8hrPR1a5AxFvxGB45slKEjuP0_72cM_vEYICgZn3PcUUkHU9wghJO9wjZ6kiIKK1h5f2K9g-Iprv9BbTOWUODu1HoLIvg2TtGsINxcRYy_8LW1RtvQc1b4dBPoopaEH4no-BIzp0E5E",
	}

	assert.Equal(t, config.UserGuid(), "772dda3f-669f-4276-b2bd-90486abe1f6f")
}

func TestUserGuidWithInvalidAccessToken(t *testing.T) {
	config := Configuration{}

	config.AccessToken = "bearer"
	assert.Empty(t, config.UserGuid())

	config.AccessToken = "bearer eyJhbGciOiJSUzI1NiJ9"
	assert.Empty(t, config.UserGuid())
}
